import React, { useState } from 'react';
import { firestore, auth } from '../firebase'; // Corrected import path for firestore and auth
import firebase from 'firebase';

function SendMessage() {
    const [msg, setMsg] = useState('');

    async function sendMessage(e) {
        e.preventDefault();
        const { uid } = auth.currentUser;

        await firestore.collection('messages').add({
            text: msg,
            uid,
            createdAt: firebase.firestore.FieldValue.serverTimestamp() // Corrected serverTimestamp spelling
        });
        setMsg('');
    }

    return (
        <div>
            <form onSubmit={sendMessage}>
                <Input value={msg} onChange={(e) => setMsg(e.target.value)} placeholder="Message..." /> {/* Corrected assignment in onChange */}
                <Button type="submit">Send</Button> {/* Corrected Button component */}
            </form>
        </div>
    );
}

export default SendMessage;
