            text: msg,
            uid, 
            createdAt: firebase.firestore.FieldValue.serverTimeStamp
        })
    }
    return (
        <div>
            <form onSubmit={sendMessage}>
                <Input value={msg} onChange={(e) =>setMsg=(e.target.value)} placeholder="Message..."/>
                <button type="submit">Send</button>
            </form>
        </div>
